class Quic:
    def __init__(self) -> None:
        self.public_flag='ce'#la version de QUIC
        self.version='00000001'

        #Connection id info
        self.dcid_length='08' 
        self.dcid='62e114195928091a' #Longueur de l'id de destination
        self.scid_length='00' #longueur de l'id source
        
        #Token Info
        self.token_length='40' #longueur du token
        self.token = '46006654c99fc9e601a3a8a89c596bffff21d131800ec744fa5e5ae06b3a084b3a455e9a8b3a765e850368d87fbc7fbb350903419cacf8c387ea39b0c5a2dfe1' 
        
        self.packet_length='6d33' #longueur du paquet
        self.packet_number='83'
        self.payload=''

        self.header=self.public_flag + self.version + self.dcid_length + self.dcid +self.scid_length +self.token_length+self.token+self.packet_length+ self.packet_number+self.payload
      




